{
  "layout": {
    "name": "TestLayoutGBR_030423",
    "comment": "TestLayoutGBR",
    "applies_to": [
      {
        "country_iso": "GBR",
        "datasets": [
          "gb-address"
        ]
      }
    ],
    "options": {
      "variation": 1,
      "flatten_diacritics": false,
      "enable_enhanced_layout": false,
      "display_enhanced_info_on_picklist": false,
      "enable_intelligent_layout": false,
      "capitalise_unused": false,
      "separate_elements": true,
      "element_separator": {
        "default": ", ",
        "configuration_by_element": {
          "buildingNumber": ", ^ ",
          "subBuildingNumber": ", ^ ",
          "postcode": " ^ ",
          "deliveryPointSuffix": " ^ ",
          "country": " ^ ",
          "pafAddressKey": "^",
          "pafOrganisationKey": "^"
        }
      },
      "terminate_lines": false,
      "line_terminator": {
        "default": ", ",
        "configuration_by_element": {
          "postcode": "^",
          "deliveryPointSuffix": "^",
          "country": ".^."
        }
      },
      "pad_lines": false,
      "multiple_dataplus_delimiter": "|",
      "abbreviate_item": [
        "thoroughfareName",
        "county"
      ],
      "capitalise_item": [
        "postcode",
        "country"
      ]
    },
    "lines": [
      {
        "line_name": "Line 1",
        "max_width": 255
      },
      {
        "line_name": "Line 2",
        "max_width": 255
      },
      {
        "line_name": "Line 3",
        "max_width": 255
      },
      {
        "line_name": "Locality",
        "max_width": 255,
        "elements": [
          {
            "element_name": "town"
          }
        ]
      },
      {
        "line_name": "Region",
        "max_width": 255
      },
      {
        "line_name": "Postal Code",
        "max_width": 255,
        "elements": [
          {
            "element_name": "postcode"
          }
        ]
      },
      {
        "line_name": "Country",
        "max_width": 255,
        "elements": [
          {
            "element_name": "country"
          }
        ]
      }
    ]
  }
}
